mapper.delete:
delete	tbl_reply
where	replyNum = #{replyNum}
	and userId = #{userId}

mapper.select:
select	userId
from	tbl_reply
where	replyNum = #{replyNum}

mapper.update:
update	tbl_reply
set		replyCon = #{replyCon}
where	replyNum = #{replyNum}
and		userId = #{userId}

mapper.delete:
delete tbl_cart
where  cartNum = #{cartNum}
and    userId = #{userId}
